import sys

def main():
    r, g, b = map(int, sys.stdin.readline().split())

    # str(r) + str(g) + str(b) would be a string
    # int(str(r) + str(g) + str(b)) would be an integer
    if int(str(r) + str(g) + str(b)) % 4 == 0:
        print("YES")
    else:
        print("NO")

if __name__ == '__main__':
    main()

