n, k = map(int, input().split())

if n == 1:
    print(k)
elif n == 2:
    print(k*(k-1))
else:
    print(k*(k-1)**(n-1))

def main():
    n, k = map(int, input().split())
    dp = [0] * (n + 1)
    dp[0] = 1
    for i in range(n):
        for j in range(i + 1):
            if j == 0:
                dp[j] = dp[j] * k
            else:
                dp[j] = (dp[j] + dp[j - 1]) * (k - 1)
    print(dp[-1])

if __name__ == '__main__':
    main()
