import sys

def main():
    input_list = sys.stdin.readline().split()
    a = int(input_list[0])
    b = int(input_list[2])
    if input_list[1] == '+':
        print(a + b)
    elif input_list[1] == '-':
        print(a - b)

if __name__ == '__main__':
    main()

