import sys

def main():
    N = int(sys.stdin.readline())
    words = []
    for i in range(N):
        words.append(sys.stdin.readline().strip())
    if len(set(words)) != N:
        print("Player " + str(1 + words.index(words[-1])) + " lost")
    elif words[0][0] != words[1][-1]:
        print("Player 1 lost")
    else:
        for i in range(1, N-1):
            if words[i][0] != words[i+1][-1]:
                print("Player " + str(i+1) + " lost")
                break
        else:
            print("Fair Game")

main()
