import sys

def main():
    f = sys.stdin.readline().strip().split('/')
    a = int(f[0])
    b = int(f[1])
    c = 5*a - 160*b
    d = 9*b
    gcd = find_gcd(abs(c), d)
    print(str(c // gcd) + '/' + str(d // gcd))

def find_gcd(a, b):
    if b == 0:
        return a
    else:
        return find_gcd(b, a % b)

main()

import sys
import math

def main():
    a, b = [int(i) for i in sys.stdin.readline().split('/')]
    c = 5 * (a - 32)
    d = 9 * b
    gcd = math.gcd(c, d)
    if c == 0:
        print(0, 1, sep='/')
    else:
        print(c // gcd, d // gcd, sep='/')

if __name__ == "__main__":
    main()
