def main():
    capacity, num_stations = [int(x) for x in input().split()]
    for i in range(num_stations):
        left, entered, waited = [int(x) for x in input().split()]
        if left + entered + waited > capacity:
            print("impossible")
            return
        if left + entered + waited < capacity:
            print("impossible")
            return
        if left - entered - waited < 0:
            print("impossible")
            return
        if left - entered - waited > 0 and i == num_stations - 1:
            print("impossible")
            return
    print("possible")

main()

