def main():
    n = int(input())
    a = []
    for i in range(n):
        a.append(int(input()))
    a = sorted(a,reverse = True)
    max = a[0] * a[0]
    for i in range(1,n):
        max += a[i] * a[i]
    print(max)

if __name__ == '__main__':
    main()

class InputData:
    def __init__(self, input_data):
        lines = input_data.split('\n')
        self.n = int(lines[0])
        self.a = [int(x) for x in lines[1:]]

class MaxExpression:
    def __init__(self, data):
        self.n = data.n
        self.a = data.a

    def max_expression(self):
        a = self.a
        n = self.n
        a.sort()
        return sum(a[:n//2])**2 + sum(a[n//2:])**2

import sys

if __name__ == '__main__':
    if len(sys.argv) > 1:
        file_location = sys.argv[1].strip()
        with open(file_location, 'r') as input_data_file:
            input_data = input_data_file.read()
        print(MaxExpression(InputData(input_data)).max_expression())
    else:
        print('This test requires an input file.  Please select one from the data directory. (i.e. python solver.py ./data/max_expression_1)')
