import fileinput

def main():
    for line in fileinput.input():
        line = line.strip()
        compact = ""
        cur = line[0]
        for i in range(1, len(line)):
            if line[i] != cur:
                compact += cur
                cur = line[i]
        compact += cur
        print(compact)

if __name__ == '__main__':
    main()

