def peter_pan_frames(char):
    """
    Returns a string with the Peter Pan frame for the given character.
    """
    return ".#.\n.#.\n" + char + "\n.#.\n.#."

def wendy_frames(char):
    """
    Returns a string with the Wendy frame for the given character.
    """
    return ".*.\n.*.\n" + char + "\n.*.\n.*."

def get_frames(word):
    """
    Returns a list of frames for each character in the given word.
    """
    frames = []
    for i, char in enumerate(word):
        if i % 3 == 2:
            frames.append(wendy_frames(char))
        else:
            frames.append(peter_pan_frames(char))
    return frames

def main():
    word = input()
    frames = get_frames(word)
    for i in range(5):
        print("".join([frame.split("\n")[i] for frame in frames]))

if __name__ == "__main__":
    main()

