def main():
    n = int(input())
    times = list(map(int, input().split()))

    times.sort()
    total = 0
    for i in range(n):
        total += times[i] * (n-i)
    print(total)

if __name__ == "__main__":
    main()

def main():
    teams = int(input())
    times = [int(x) for x in input().split()]
    times.sort(reverse=True)

    first = 0
    second = 0
    for i in range(teams):
        if i % 2 == 0:
            first += times[i]
        else:
            second += times[i]

    print(first + second)

main()
