def main():
    n = int(input())
    d = [int(x) for x in input().split()]
    days = [0]*366
    for day in d:
        days[day] += 1
    for i in range(1, 366):
        days[i] += days[i-1]
    cleanups = 0
    for i in range(366):
        if days[i] >= 20:
            cleanups += 1
            days[i] = 0
    print(cleanups)

if __name__ == "__main__":
    main()

#CODE
n = int(input())
days = [int(x) for x in input().split()]

if n == 0:
    print('0')
else:
    c = 0
    for i in range(n):
        if days[i] + 20 >= days[-1]:
            c += 1
            break
        for j in range(i+1, n):
            if days[j] - days[i] > 20:
                i = j - 1
                c += 1
                break
    print(c)
