import sys

def pig_latin(sentence):
    words = sentence.split()
    new_words = []
    for word in words:
        if word[0] in 'aeiou':
            new_words.append(word + 'yay')
        else:
            new_words.append(word[1:] + word[0] + 'ay')
    return ' '.join(new_words)

def main():
    for line in sys.stdin:
        print(pig_latin(line))

if __name__ == '__main__':
    main()

import sys

def piglatin(word):
    vowels = 'a e i o u y'.split()
    if word[0] in vowels:
        return word + 'yay'
    else:
        for i, char in enumerate(word):
            if char in vowels:
                return word[i:] + word[:i] + 'ay'

def main():
    for line in sys.stdin:
        print(' '.join(map(piglatin, line.split())))

if __name__ == '__main__':
    main()

