import sys

def main():
    n = int(sys.stdin.readline())
    s = sys.stdin.readline()
    if n == 1:
        print("YES")
        print(0)
    else:
        if s[0] == s[n - 1]:
            print("NO")
        else:
            print("YES")
            print(1, end="")
            for i in range(n - 2):
                if (s[i] == s[i + 1] and s[i] == s[n - 1]) or (s[i] == s[i + 1] and s[i] == s[0]):
                    print(0, end="")
                else:
                    print(1, end="")
            print(0)

if __name__ == "__main__":
    main()

def solve(s):
    if len(s) == 1:
        return 'YES\n0'
    n = len(s)
    s = list(s)
    ans = [0] * n
    for i in range(1, n):
        if s[i] < s[i-1]:
            ans[i] = 1
            s[i] = chr(ord(s[i]) + 2)
    for i in range(1, n):
        if s[i] < s[i-1]:
            return 'NO'
    return 'YES\n' + ''.join(map(str, ans))

n = int(input())
s = input()
print(solve(s))
