def check(n, m, a):
    a = sorted(a, reverse=True)
    for i in range(1, n+1):
        if sum(a[:i]) >= m:
            return i
    return -1

n, m = map(int, input().split())
a = list(map(int, input().split()))
print(check(n, m, a))

n, m = map(int, raw_input().split())
cups = map(int, raw_input().split())

pages = 0
days = 0

while pages < m:
    days += 1
    pages_today = 0
    for i in range(n):
        if cups[i] > 0:
            pages_today += cups[i] - days + 1
            if pages_today < 0:
                pages_today = 0
    pages += pages_today

if pages >= m:
    print days
else:
    print -1
