def numline(f = int):
  return map(f, input().split())

n, k, x = numline()
a = [i for i in numline()]

print(sum(a[:len(a) - k]) + k * x)

n, k, x = list(map(int, input().split()))
a = list(map(int, input().split()))

ans = 0
for i in range(n):
    if n - i - 1 < k:
        ans += min(a[i], x)
    else:
        ans += a[i]
print(ans)

