class Solution:
    def generateTheString(self, n: int) -> str:



        if n%2 == 0:

            return ''.join(['a']*(n-1) + ['b'])

        else:
            if n == 1:
                return 'a'
            else:
                return ''.join(['a']*(n-2) + ['bc'])

class Solution:
    def generateTheString(self, n: int) -> str:
        if (n&0x1)==1:
            return 'a'*n
        else:
            return (n-1)*'a'+'b'

