class Solution:
     def convertToTitle(self, n):
         """
         :type n: int
         :rtype: str
         """
         return "" if n == 0 else self.convertToTitle(int((n-1)/26)) + chr((n-1)%26+ord('A'))

class Solution:
     def convertToTitle(self, num):
         res = []

         while num > 0:
             res.append(chr(ord('A') + (num - 1) % 26))
             num = (num - 1) // 26
         res.reverse()
         return "".join(res)

         """
         :type n: int
         :rtype: str
         """

class Solution:
     def convertToTitle(self, n):
         """
         :type n: int
         :rtype: str
         """

         if n <= 26:
             return chr(n + 64)

         out = ''
         r = n
         while r >= 1:
             out += chr((r-1) % 26 + 65)
             r = (r - 1) // 26

         return out[::-1]

class Solution:
     def convertToTitle(self, n):
         """
         :type n: int
         :rtype: str
         """
         ret = []
         while n > 0:
             n -= 1
             tmp = n % 26
             n = int(n/26)
             ret.append(chr(ord('A') + tmp))
         return ''.join(reversed(ret))

