def minion_game(string):
    n=len(string)
    player1,player2=0,0
    for i in range(0,n):
        if(string[i] in 'AEIOU'):
            player1+=n-i
        else:
            player2+=n-i
    if(player1>player2):
        return 'Kevin '+ str(player1)
    elif(player1==player2):
        return 'Draw'
    else:
        return 'Stuart '+str(player2)

vowels = ['A', 'E', 'I', 'O', 'U']

def minion_game(string):
    score_kevin = 0
    score_stuart = 0

    for ind in range(len(string)):
        if string[ind] in vowels:
            score_kevin += len(string) - ind
        else:
            score_stuart += len(string) - ind

    if score_kevin > score_stuart:
        return 'Kevin '+ str(score_kevin)
    elif score_kevin < score_stuart:
        return 'Stuart '+ str(score_stuart)
    else:
        return 'Draw'
