from functools import partial
from re import compile

REGEX1 = partial(compile(r"(?<=\w)(?=\b)").sub, "!!!!")
trans = str.maketrans("AEIOU", "@****")

def gordon(a):
    return REGEX1(a.upper()).translate(trans)

def gordon(s):
    fmt = '{}!!!!'.format
    table = str.maketrans('AEIOU', '@****')
    return ' '.join(fmt(a.translate(table)) for a in s.upper().split())

def gordon(a):
    out, vowels = '', 'eiou'
    for i in a:
        if i == 'a':
            out += '@'
        elif i in vowels:
            out += '*'
        elif i == ' ':
            out += '!!!! '
        else:
            out += str(i).upper()
    return '%s!!!!' % out

import re

def gordon(a):
    return re.sub("[EIOU]", "*", " ".join(word.upper() + "!!!!" for word in a.split()).replace("A", "@"))
