def largest_sum(arr):

    largest = 0
    temp = 0

    for num in arr:
        temp += num
        if temp < 0:
            temp = 0
        elif temp > largest:
            largest = temp

    return largest

def largest_sum(arr):
    if len(arr) == 0:
        return 0 
    cache = [arr[0]]
    for i in range(1, len(arr)):
        cache.append(max(arr[i], cache[-1] + arr[i]))
    return 0 if max(cache) < 0 else max(cache)

def largest_sum(arr):
    best_sum = 0
    current_sum = 0
    for item in arr:
        current_sum += item
        if current_sum < 0:
            current_sum = 0
        else:
            if current_sum > best_sum:
                best_sum = current_sum
    return best_sum

def largest_sum(arr):
    ans,sum = 0,0
    for i in range(len(arr)):
        sum = max(arr[i], arr[i] + sum)
        ans = max(ans,sum)
