def is_nice(arr):
    s = set(arr)
    return bool(arr) and all( n+1 in s or n-1 in s for n in s)

def is_nice(arr):
    for x in arr:
        if x + 1 not in arr and x - 1 not in arr:
            return False
    return True and len(arr) > 0

def is_nice(arr):
    if len(arr) == 0:
        return False
    for n in arr:
        if n-1 not in arr and n+1 not in arr:
            return False
    return True

