def count(a, t, x):
    if x == 0:
        return a.count(t)
    return sum((t-e)%x == 0 for e in a)

def count(lst, target, mod):
    if mod == 0: return lst.count(target)
    return sum((n - target) % mod == 0 for n in lst)

def count(a, t, x):
    result=0
    for n in a:
        if n==t or (x!=0 and (n-t)%x==0):
            result+=1
    return result
