    s = " ".join(s)
    result = ""

    for letter in s:
        if letter.islower():
            result = result + letter.upper()
        elif letter.isupper():
            result = result + letter.lower()
        else:
            result = result + letter

    return result

def string_transformer(s):

    array = []

    if s is '':
        return ''

    for i in s:
        if i == i.lower():
            array.append(i.upper())
        else:
            array.append(i.lower())

    result = ''.join(array)
    return ' '.join(result.split(' ')[::-1])

