def split_by_value(k, elements):
    res = [[], []]
    for x in elements:
        res[k<=x].append(x)
    return res[0] + res[1]

def split_by_value(k, elements):
    l = []
    elem2 = elements.copy()
    for i in elements:
        if i < k:
            l.append(i)
            elem2.remove(i)

    return l + elem2

from itertools import chain

def split_by_value(k, arr):
    lst = [[],[]]
    for v in arr: lst[v>=k].append(v)
    return list(chain(*lst))
