import re

def remove(s):
    return re.sub(r'!+(?!!*$)', '', s)

def remove(s):
    end_i = len(s.rstrip('!'))
    return s[:end_i].replace('!', '') + s[end_i:]

def remove(s):
    num = 0
    for letter in s[::-1]:
        if letter != "!":
            break
        num += 1
    return s.replace("!", "") + "!" * num

def remove(s):
    num_of_marks = 0
    while s[-num_of_marks - 1] == '!':
        num_of_marks += 1
    s = s.replace('!', '')
    return s + '!' * num_of_marks

import regex

def remove(s):
    return regex.sub(r'!++(?!$)', '', s)

def remove(s):
    string = ''
    for i in range(len(s)):
        if s[i] != '!':
            string += s[i]
    count = 0
    for i in range(-1,-(len(s)-1),-1):
        if s[i] == '!':
            count += 1
        else:
            break
    return string + count*'!'
