from textwrap import wrap


def split_in_parts(s, part_length): 
    return " ".join(wrap(s,part_length))

def split_in_parts(s, part_length):
    newS = ''
    for i in range(len(s)):
        newS += f' {s[i]}' if i != 0 and i % part_length == 0 else s[i]

    return newS

def split_in_parts(s, part_length):
    cnt = 0
    answer = ""
    for i in s:
        if cnt < part_length:
            answer += i
            cnt += 1
        else:
            answer = answer + " "  + i
            cnt = 1
    return answer

def split_in_parts(s, part_length): 
    r = []
    for x in range(0,len(s),part_length):
        r += [s[x:x+part_length]]

    return  " ".join(r)
