def pop_shift(s):
    l1 = list(s); l2 = []; l3 = []
    while len(l1) > 1:
        l2.append(l1.pop())
        l3.append(l1.pop(0))
    return ["".join(l2),"".join(l3),"".join(l1)]

def pop_shift(s):
    il, ir = len(s) // 2, (len(s) + 1) // 2
    return [s[:ir-1:-1], s[:il], s[il:ir]]

def pop_shift(s):
    half, odd_len = divmod(len(s), 2)
    return [s[half + odd_len:][::-1], s[:half], s[half] if odd_len else '']

def pop_shift(string):
    mid = len(string) // 2
    return [string[::-1][:mid], string[:mid], string[mid : len(string)-mid]]

def pop_shift(s):
    i = len(s)//2
    return [s[-1:-i-1:-1], s[:i], s[i:i+len(s)%2]]

def pop_shift(s):
    i, j = divmod(len(s), 2)
    return [s[i+j:][::-1], s[:i], s[i:i+j]]

def pop_shift(stg):
    d, m = divmod(len(stg), 2)
    return [stg[:d+m-1:-1], stg[:d], stg[d] if m else ""]

def pop_shift(str):
    l = len(str)
    return [str[::-1][:l//2], str[:l//2], str[l//2]*(l%2)]
