from math import factorial

def routes(n):
    return n > 0 and factorial(2*n) // factorial(n)**2

def routes(n):
  if(n < 1):
    return(0);

  res = 1;
  for i in range(1, n + 1):
    res = res*(n + i)//i;

  return(res);

