def solve(xs):
    x, y, z = sorted(xs)
    return min(x + y, (x + y + z) // 2)

def solve(arr):
    a,b,c = sorted(arr)
    s = a+b
    if s < c:
        return s
    else:
        return c + int((s-c)/2)

def score(a,b,c):
    if a+b<c: return a+b
    return (a+b-c)//2+c

def solve(arr):
    a,b,c = arr
    return min(score(a,b,c),score(b,c,a),score(c,a,b))

def solve(arr):
    a, b, c = sorted(arr)
    return (a + b + min(a+b, c)) // 2

def solve(arr):
    arr.sort()
    d = arr[2]-arr[1]
    if d>arr[0]:
        return arr[0] + arr[1]
    return arr[1]+d +(arr[0]-d)//2
