def hex_hash(code):
    first = ''
    for i in code :
        first += hex(ord(i))[2:]
    second = ''
    for i  in first :
        if i.isdigit() :
            second += i
    last = 0
    for i in second:
        last += int(i)
    return last

def hex_hash(code):
    r=0
    for c in code:
        for d in hex(ord(c))[2:]:
            if d.isdigit():
                r+=int(d)
    return r

def hex_hash(code):
    res=''.join(list(map(hex, map(ord,code))))
    return sum(int(x) for x in res if x.isdigit())

from re import sub
def hex_hash(code):
    return sum(sum(map(int,sub("[^0-9]","",hex(ord(c))))) for c in code)
