def interest(principal, interest, periods):
    return [round(principal * (1 + interest * periods)),
            round(principal * (1 + interest) ** periods)]

def interest(p,r,n):
    simple = p + p * r * n
    while n > 0:
        n -= 1
        p += p * r
    return [round(simple), round(p)]

def interest(p,r,n):
    return [round(p+p*r*n), compound(p,r,n)]

def compound(p,r,n):
    return round(p) if n<1 else compound(p+p*r,r,n-1)
