from itertools import groupby
import re


def fire_and_fury(tweet):
    if set(tweet) > set("EFIRUY") or ("FIRE" not in tweet and "FURY" not in tweet):
        return "Fake tweet."
    return " ".join(translate(group) for group in groupby(re.findall(r"FIRE|FURY", tweet)))


def translate(group):
    word, repeat = group[0], len(tuple(group[1])) - 1
    base, more = {"FURY": ("I am {}furious.", "really "), "FIRE": ("You {}are fired!", "and you ")}[word]
    return base.format(more * repeat)

from re import findall
from itertools import groupby


def fire_and_fury(tweet):
    s, match = [], findall(r"FIRE|FURY", tweet)
    if not match or not all([x in ["E", "F", "I", "R", "U", "Y"] for x in tweet]):
        return "Fake tweet."
    for i in groupby(match):
        s.append((i[0], len(list(i[1]))))
    s = translated(s)
    return " ".join(s)


def translated(s):
    string = []
    for i in s:
        if i[0] == "FIRE":
            string.append("You "+ "and you " * (i[1] - 1) + "are fired!")
        elif i[0] == "FURY":
            string.append("I am " + "really " * (i[1] - 1) + "furious.")
    return string

import itertools
import re

MEANINGS = {
    'FIRE': [lambda i: "You are fired!", lambda i: f"You{' and you' * i} are fired!"],
    'FURY': [lambda i: "I am furious.", lambda i: f"I am{' really' * i} furious."],
}

def fire_and_fury(tweet):
    if re.search('[^EFIRUY]', tweet):
        tweet = ''
    return ' '.join((
        MEANINGS[key][n > 1](n-1)
        for key, grp in itertools.groupby(re.findall('FIRE|FURY', tweet))
        for n in [sum(1 for _ in grp)]
    )) or 'Fake tweet.'

import re
def fire_and_fury(tweet):
    if re.search(r'[^EFIRUY]',tweet):
        return 'Fake tweet.'
    tweet = "".join(re.findall(r'FIRE|FURY',tweet))
    return re.sub(r'(FIRE)+|(FURY)+',change,tweet).strip(" ") if tweet else 'Fake tweet.'


def change(m):
    m = m.group()
    if 'FURY' in m:
        return "I am "+"really "*(m.count("FURY")-1)+ "furious." + " "
    if 'FIRE' in m:
        return "You "+"and you "*(m.count("FIRE")-1) + "are fired!" + " "

