import textwrap

def align_right(text,width):
    return "\n".join([l.rjust(width, ' ') for l in textwrap.wrap(text, width)])

def align_right(text, width):
    res = []
    line = []

    for word in text.split():
        if len(' '.join(line + [word])) <= width:
            line.append(word)
        else:
            res.append(line)
            line = [word]

    res.append(line)

    return '\n'.join(' '.join(line).rjust(width) for line in res)

from textwrap import TextWrapper


def align_right(text, width):
    tw = TextWrapper(width, break_on_hyphens=False)
    return '\n'.join(a.rjust(width) for a in tw.wrap(text))

import re

def align_right(text, width):
    return '\n'.join( ' '*(width-len(line)) + line for line,sep in re.findall(r'(.{,' + str(width) + '})( |$)', text) if line )

def align_right(text, width):
    answer = []
    splitted = text.split(' ')
    temp = ''
    i = 0
    while i < len(splitted):
        while i < len(splitted) and len(temp + splitted[i]) < width + 1:
            temp += splitted[i] + ' '
            i += 1
        temp = temp.strip()
        answer.append(' ' * (width - len(temp)) + temp)
        temp = ''
    return '\n'.join(answer)

def align_right(text,width):
    k = text.split()

    out = ''
    last = []
    for x in k:
        if len(x + out) <= width:
            out += x + ' '
        else:
            last.append(out)
            out = x+' '

    last.append(out)
    return '\n'.join(' ' * (width - len(x.rstrip())) + x.rstrip() for x in last)

import textwrap

def align_right(text, width):
    wrapper = textwrap.TextWrapper(width=width, break_on_hyphens=True)
    return '\n'.join(line.rjust(width) for line in wrapper.wrap(text))

def align_right(text, width):
    lines = []
    while len(text) > width:
        cut = text[:width+1].rfind(" ")
        lines.append(text[:cut].rjust(width))
        text = text[cut+1:]
    lines.append(text.rjust(width))
    return "\n".join(lines)
