def dot(n, m):
    sep = '+---' * n + '+'
    dot = '| o ' * n + '|'
    return '\n'.join([sep, dot] * m + [sep])

def dot(n,m):
    result = [("+---" * n) + "+" + "\n" + ("| o " * n) + "|" for i in range(m)]
    result.append(("+---" * n) + "+")
    return "\n".join(result)

def dot(n,m):
    sep = '---'.join( '+'*(n+1) )
    dot = ' o '.join( '|'*(n+1) )

    return f'\n{dot}\n'.join( [sep for _ in range(m+1) ])

def dot(w, h):
    sep, row = "+---" * w + "+\n", "| o " * w + "|\n"
    return (sep + row) * h + sep.strip()

def dot(n,m):
    sep = '+' + '+'.join(['---'] * n) + '+'
    bod = '|' + '|'.join([' o '] * n) + '|'
    def f():
        yield sep
        for i in range(m):
            yield bod
            yield sep
    return '\n'.join(f())
