import re
def special_number(number):
    return "Special!!" if re.match(r'^[0-5]+$', str(number)) else "NOT!!"

def special_number(n):
    condition = str(n).translate(str(n).maketrans(dict.fromkeys('012345')))
    return 'Special!!' if not condition else 'NOT!!'

