def reverse_factorial(num):
    c = 1
    while num % c == 0:
        num = num / c
        if num == 1: break
        c += 1
    else:
        return 'None'
    return '{}!'.format(c)

from math import factorial as fact
D = {fact(v):'{}!'.format(v) for v in range(1,16)}
def reverse_factorial(num):
    return D.get(num, 'None')

def reverse_factorial(num):
    if num == 1: return '1!'
    i = 0
    while num > 1:
        i += 1
        num, n = divmod(num, i)
        if n > 0: return 'None'
    return '{}!'.format(i)
