def sc(strng):
    seen = set(strng)
    return ''.join(a for a in strng if a.swapcase() in seen)

def sc(s):
    xs = set(s)
    return ''.join(c for c in s if c.swapcase() in xs)

def sc(s):
    for i in s:
        if i.islower()==True:
            if i.upper() not in s:
                s=s.replace(i,'')
        else:
            if i.lower() not in s:
                s=s.replace(i,'')
    return s

def sc(s):
    list_s = []
