def sc(width,length,gaps):
    places = width*2 + (length-2)*2
    if gaps == 0:
        return places
    div, mod = divmod(places, gaps+1)
    if mod:
        return 0
    return div

def sc(width, length, gaps):
    edges = (width + length - 2) * 2
    q, r = divmod(edges, gaps + 1)
    return q * (not r)

