def array_info(x):
    if not x:
        return 'Nothing in the array!'
    return [
        [len(x)],
        [sum(isinstance(i, int) for i in x) or None],
        [sum(isinstance(i, float) for i in x) or None],
        [sum(isinstance(i, str) and not i.isspace() for i in x) or None],
        [sum(isinstance(i, str) and i.isspace() for i in x) or None],
    ]

def array_info(x):
    TheDICT = {'len':[1,len(x)], int:[2,0], str:[4,0], float:[3,0], 'void':[5,x.count(' ')]}
    for e in x:
        if e != ' ':
            TheDICT[type(e)][1] += 1
    return [ [e[1] if e[1] else None] for e in sorted( TheDICT.values() ) ] if x else 'Nothing in the array!'

def array_info(arr):
    res = [len(arr), 0, 0, 0, 0]
    for i in arr:
        if type(i) == int:
            res[1] += 1
        elif type(i) == float:
            res[2] += 1
        elif type(i) == str:
            if len(i) * ' ' == i:
                res[4] += 1
            else:
                res[3] += 1
    return [[i] if i else [None] for i in res] if arr else 'Nothing in the array!'

def array_info(x):
    if not x: return 'Nothing in the array!'
    ints = floats = strings = whitespaces = 0
    for e in x:
        if isinstance(e, str):
            if e.isspace():
                whitespaces += 1
            else:
                strings += 1
        else:
            if float(e).is_integer():
                ints += 1
            else:
                floats += 1
    return [[c or None] for c in (len(x), ints, floats, strings, whitespaces)]
