import re

def make_sentences(parts):
    return re.sub(' ([,.])', r'\1', ' '.join(parts).replace(' ,', ',')).rstrip('.') + '.'

import re

def make_sentences(parts):
    return re.match(r'[^\.]+', ' '.join(parts).replace(' , ', ', ')).group().strip() + '.'

def make_sentences(parts):
    result = ''
    for i in range(len(parts) - 1):
        result += parts[i]
        if parts[i + 1] not in '.,':
            result += ' '
    result += parts[-1]
    return result.rstrip('.') + '.'

def make_sentences(parts):
    return '{}.'.format(
        ''.join(' ' + a if a.isalnum() else a for a in parts).strip(' .'))

def make_sentences(parts):
    n = ''
    parts = [i for i in parts if i != '.']
    for i in parts:
        if i == ',':
            n = n[:-1] + ', '
        else:
            n += i + ' '
    return n[:-1]+'.'
