def diagonal(m):
    P = sum(m[i][i] for i in range(len(m)))
    S = sum(m[i][-i-1] for i in range(len(m)))
    if P > S:
        return "Principal Diagonal win!"
    elif S > P:
        return "Secondary Diagonal win!"
    else:
        return 'Draw!'

def diagonal(matrix):
    sp, ss = map(sum, zip(*((matrix[x][x], matrix[len(matrix)-1-x][x]) for x in range(len(matrix)))))
    return "Draw!" if ss == sp else "{} Diagonal win!".format("Principal" if sp > ss else "Secondary")

def diagonal(matrix):
    principal_sum = sum(matrix[i][i] for i in range(len(matrix)))
    secondary_sum = sum(matrix[i][~i] for i in range(len(matrix)))
    if principal_sum > secondary_sum:
        return "Principal Diagonal win!"
    elif principal_sum < secondary_sum:
        return "Secondary Diagonal win!"
    else:
        return "Draw!"

def diagonal(matrix):
    res = sum(row[i] - row[-i-1] for i,row in enumerate(matrix))
    return f"{'Principal' if res > 0 else 'Secondary'} Diagonal win!" if res else "Draw!"

def diagonal(l):
    a,b = zip(*[(l[i][-i-1], l[i][i]) for i in range(len(l))])

    if sum(a) < sum(b):
        return "Principal Diagonal win!"
    elif sum(a) > sum(b):
        return "Secondary Diagonal win!"
    return 'Draw!'
