import re
def reverser(sentence):
    return ''.join(w[::-1] if ' ' not in w  else w for w in re.findall(r'(?:\w+)|(?:\s+)', sentence))

from re import compile

r = compile(r"(\W)")


def reverser(sentence: str) -> str:
    return ''.join(w[::-1] for w in r.split(sentence))

def reverser(sentence):
    emt = " "
    split_it = sentence.split(" ")
