def consecutive(arr):
    try:
        return max(arr) - min(arr) - len(arr) + 1
    except:
        return 0

def consecutive(arr):
    if len(arr) <= 1:
        return 0
    return (max(arr) - min(arr) + 1) - len(arr)

def consecutive(arr):
    if len(arr)<2 :
        return 0;
    arr.sort();
    return arr[-1]-arr[0]-len(arr)+1;

def consecutive(arr):
  l = len(arr)
  return max(arr) - min(arr) + 1 - l if l>1 else 0

def consecutive(arr):
    arr = sorted(arr)
    return arr[-1] - arr[0] - len(arr[1:]) if len(arr) > 0 else 0

def consecutive(arr):
    if not arr:
        return 0
    return max(max(arr)-min(arr)-len(arr)+1,0)
