def diff(arr):
    r = [abs(int(w.split('-')[0])-int(w.split('-')[1])) for w in arr]
    return arr[r.index(max(r))] if sum(r) else False

def difference(x):
    a, b = map(int, x.split('-'))
    return abs(a-b)


def diff(arr):
    if not arr:
        return False
    x = max(arr, key=difference)
    if difference(x) == 0:
        return False
    return x

def diff(pairs):
    max_diff = max_pair = 0
    for pair in pairs:
        a, b = pair.split('-')
        current = abs(int(a) - int(b))
        if current > max_diff:
            max_diff = current
            max_pair = pair
    return max_pair

