def find_short(s):
    sList = s.split()
    shortestLength = len(sList[0])
    for item in sList:
        if len(item) < shortestLength:
            shortestLength = len(item)
    return shortestLength

def find_short(s):
    l = 100000;
    a = s.split(" ")
    for i in a:
        if(len(i)<l):
            l = len(i)
    return l # l: shortest word length
