def current(usefulness):
    return sum(usefulness)

def needed(months):
    if months == 0:
        return 100
    else:
        return 0.85 * needed(months - 1)

def match(usefulness, months):
    if current(usefulness) < needed(months):
        return "No match!"
    else:
        return "Match!"

def match(usefulness, months):
    match = sum(usefulness) >= 100 * 0.85 ** months
    return 'Match!' if match else 'No match!'

def match(usefulness, months):
    decay, needs = 0.15, 100
    return "Match!" if needs * (1 - decay)**months <= sum(usefulness) else "No match!"
