from urllib.request import urlopen
from bs4 import BeautifulSoup

def get_member_since(username):
    page = urlopen('https://www.codewars.com/users/' + username)
    soup = BeautifulSoup(page, 'html.parser')

    for i in soup.select('.stat'):
        if ("Member Since" in i.text.strip()):
            text = i.text.strip()
            final = text[13:]
            return final

import urllib.request, re

def get_member_since(user):
    URL = "https://www.codewars.com/users/"
    page = urllib.request.urlopen(URL + user)
    join_date = re.search("[A-Z][a-z]+ \d+", str(page.read()))
    return join_date.group()

import urllib.request
import bs4
import re
def get_member_since(username):
    soup = bs4.BeautifulSoup(urllib.request.urlopen(f'https://www.codewars.com/users/{username}'))
    return re.search(r'(?<=Member Since:</b>).+?(?=</div>)',str(soup.findAll('div',attrs={'class':'stat'}))).group()
