def only_one(*args):
    count = 0
    for item in args:
        if item:
            count += 1
        if count == 2:
            return False
    return True if count == 1 else False

from collections import Counter
def only_one(*booleans):
    return Counter(booleans)[True] == 1

def only_one(*bool):
    sum = 0
    for value in bool:
        if value:
            sum += 1
    if sum == 1:
        return True
    else:
        return False
