def histogram(values, bin_width):
    k = []
    end = max(values) if values else -1
    bin = [i for i in range(bin_width ,end + bin_width+1, bin_width)]
    for i in bin:
        count = 0
        for val in values:
            if i-bin_width <= val < i:
                count += 1
        k += [count]
    return k

import math
def histogram(values, bin_width):
    if values == []:
        return []

    hist = [0] * math.ceil((max(values) + 1) / bin_width)

    for v in values:
        hist[math.floor(v / bin_width)] += 1

    return hist

from itertools import groupby
def histogram(values, bin_width):
    c={k:len(list(g)) for k,g in groupby(sorted(values),key=lambda x:x//bin_width)}
    return [c.get(i,0) for i in range(max(c)+1)] if c else []

def histogram(values, bin_width):
    if len(values) == 0:
        return []
    list = [values.count(digit) for digit in range(0, max(values) + 1)]
    return [sum(list[digit:digit+bin_width]) for digit in range(0, len(list), bin_width)]
